sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (newValue === '') {
      return;
   }

var ga = new GlideAjax('GetEmailAddress');
	ga.addParam('sysparm_name', 'getEmail');	
	ga.addParam('sysparm_userID', newValue);

	ga.getXML(callback);

   //Type appropriate comment here, and begin script below

}

function callback(result) {
	var email = result.responseXML.documentElement.getAttribute('answer');
	g_form.clearValue('u_requested_for_email');
	g_form.setValue('u_requested_for_email', email);
}
